	private Gson gson = new Gson();

	public List<Palestra> getAll() {
		Palestra[] palestrasArray = gson.fromJson(jsonPalestras,
				Palestra[].class);
		return Arrays.asList(palestrasArray);
	}

	public Palestra getById(int id) {
		Palestra[] palestrasArray = gson.fromJson(jsonPalestras,
				Palestra[].class);
		List<Palestra> palestras = Arrays.asList(palestrasArray);

		for (int i = 0; i < palestras.size(); i++) {
			if (!palestras.get(i).id.isEmpty()
					&& Long.valueOf(palestras.get(i).id) == id) {
				return palestras.get(i);
			}
		}
		return null;
	}
